    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {
        Object columnNames[] = {"Image", "ID", "Item Title", "Price" };
        JTable table = null;
		try {
      //Used DefaultTableModel since it has pre-coded add,delete, insert, etc. functions
			 DefaultTableModel model = new DefaultTableModel(is.search(jTextField8.getText()), columnNames) {
                 @Override
                 public Class<?> getColumnClass(int column) {
                     switch(column) {
                         case 0:
                         	return ImageIcon.class;
                         default: return Object.class;
                     }
                 }
             };
			table = new JTable(model);
			table.setAutoCreateRowSorter(true);
			table.setRowHeight(200);
			 jScrollPane1.setViewportView(table);
		} catch (Exception e) {
			jTextArea1.setText(e.getMessage());
		}

       }

    /**
     * @param args the command line arguments
     */
    public void loadUI(String user) {
    	Scanner scanner = new Scanner(System.in);
    	System.out.println("Logged in as " + user);

    	System.out.print("Please enter input data file name: ");
    	//get user name
    	String input = scanner.nextLine();


      // initialze log file
		String logFile = "log.txt";

		is = new InventorySystem(user, logFile);
		if (input != null)
			is.readLine(input);



    }
    private void jTabbedPane1MouseClicked(java.awt.event.MouseEvent evt) {
    	if(is.isAdmin()) {
    	// read log file and update transactional logs.
   	 jTextArea2.setText(is.readLogFile());
    	} else {
    		jTextArea2.setText("You are not authorized to view this log. Please login as Admin");
    	}
    }
